import React, {useEffect, useRef, useCallback } from 'react';
import { connect, useDispatch, useSelector} from "react-redux";

import EditWindow from '../../components/EditWindow/EditWindow'
import {hendlerOnChangeInput_1, hendlerOnChangeInput_2, hendlerOnChangeInput_3, hendlerOnChangeInput_4, hendlerOnChangeInput_5} from "../../store/inputCreateNewData/actions/actionInputCreateNewData"

function ContEditWindow (props){
    
    const stateTable = useSelector(state => state.stateTable);
    const labels = Object.keys(stateTable[0]);
    const [x1, x2, x3, x4, x5] = [...labels];
    const idName = "WindowNewData";
    
    const refInput1 = useRef();
    
    useEffect(()=>{
        refInput1.current.focus();
	       return () =>{};
    }, [])
    
    
    const value1 = useSelector(state => state.inputCreateNewData.uid)
    const value2 = useSelector(state => state.inputCreateNewData.name)
    const value3 = useSelector(state => state.inputCreateNewData.comment)
    const value4 = useSelector(state => state.inputCreateNewData.classViewProperty)
    const value5 = useSelector(state => state.inputCreateNewData.dbTable)
    
    const handlerClick = ()=>{
        console.log('handlerClick');
    }
    
        
    const dispatch = useDispatch();
      const handler1 = useCallback(() => {
                    dispatch(hendlerOnChangeInput_1());
        }, []);
    
    return(
        <EditWindow 
        
                    id1 = {x1 + idName}
                    ref1 = {refInput1}
                    label1 = {x1}
                    type1 = "number"
                    placeholder1 = {x1}
                    value1 = {value1}
                    onchange1 = {handler1}
                    tabIndex1 = '1'
                    autofocus1 = "autofocus"
        
                    id2 = {x2 + idName}
                    label2 = "Наименование"
                    type2 = "text"
                    placeholder2 = "Введите наименование"
                    value2 = {value2}
                    onchange2 = {handler1} 
                    tabIndex2 = '2'
        
                    id3 = {x3 + idName}
                    label3 = "Комментарии"
                    cols = "20"
                    rows = "5"
                    placeholder3 = "Оставьте комментарии"
                    value3 = {value3}
                    onchange3 = {handler1} 
                    tabIndex3 = '3'
        
                    id4 = {x4 + idName}
                    label4 = "Класс"
                    type4 = "text"
                    placeholder4 = "Укажите класс"
                    value4 = {value4}
                    onchange4 = {handler1} 
                    tabIndex4 = '4'
                    autofocus4 = ""
        
                    id5 = {x5 + idName}
                    label5 = {x5}
                    type5 = "text"
                    placeholder5 = {x5}
                    value5 = {value5}
                    onchange5 = {handler1} 
                    tabIndex5 = '5'
                    autofocus5 = ""
        
                    classNameLabel = ''
                    classNameInput = ''
                    classNameTextarea = ''
        
                    nameButtonSave = "Сохранить"
                    nameButtonClose = "Отмена"
                    onclick1 = {handlerClick}
                    onclick2 = {handlerClick}
                    classWrapper = ""
                    classButtonWrapper = ""
                    classButton = ""
                    classButtonX = ""
            />
    )
}

export default connect()(ContEditWindow)
