import React from 'react';


const hoverDescription = (Component, propIsOpen) => {

    return class extends React.Component {

        state = { isOpenDescription: false };
        mouseOver = () => this.setState({ isOpenDescription: true });
        mouseOut = () => this.setState({ isOpenDescription: false });

        render() {

            const props = {
                [propIsOpen]: this.state.isOpenDescription,
                ...this.props,
            }

            return(

                <div onMouseOver={this.mouseOver} onMouseOut={this.mouseOut}>
                    <Component {...props} />
                </div>
                )
        }
    }

}
export default hoverDescription
