import { takeEvery, select, put, call } from 'redux-saga/effects';
import {getRequestServer} from '../../store/serverViewType/actions/actionViewType';
import {url_PUT} from "../../constans/url"
import {START_PUT_REQUEST} from '../../store/windowCreateNewData/types';
import {startGetRequestServer} from '../../store/serverViewType/actions/actionViewType';


export function fetchDataPost(url, method, body){
    return fetch( url,{
                
                method: method,
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json'
                  },
                body: JSON.stringify(body)
                })
                .then(response => response.json())
}

export function* workerStart_PUT_RequestServer(){
    
    const stateSendServer = yield select( state => state.WindowCreateNewData.stateData);
        
    stateSendServer.uid = Number(stateSendServer.uid);
    
    try {
        
        yield call(fetchDataPost, url_PUT, 'PUT', stateSendServer);
        
        yield startGetRequestServer();
         
    } catch (error) {
       console.log('workerStart_PUT_RequestServer error ', error);
    }
 
}

export function* watchStart_PUT_RequestServer(){
    
    yield takeEvery(START_PUT_REQUEST, workerStart_PUT_RequestServer)

}
