import React, { useEffect } from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import ConsoleButton from '../../components/ConsoleButton/ConsoleButton';
import TableList from '../../components/Table/TableListEltypeClass';
import HeaderDictionary from '../../components/Header/HeaderDictionary';
import WindowConfirmDeleting from '../../components/WindowConfirmDeleting/WindowConfirmDeleting';
import WarningValueNotSelected from '../../components/WarningWindow/WarningValueNotSelected';

import {
        startGetRequestServer,
        saveDataStore,
        saveTypeRequest,
        saveNameWindow
    } from '../../store/EltypeClass/actions/actionEltypeClass';
import {
        openWindowConfirmDeleting,
        openEditWindow,
        openWindowWarning
    } from '../../store/EltypeClass/actions/actionsFlagsEltypeClass';


function EltypeClass (props){
    
    const dispatch = useDispatch();

    /* оповещение на период ожидания ответа сервера*/
    const stateLoading = useSelector(state => state.stateFlagsEltypeClass.loading);

    /*для заполнения таблицы */
    const stateTable = useSelector(state => state.stateTableEltypeClass);

    const dataDelete = useSelector(state => state.createDataEltypeClass.stateData);
    //const [flagDeleteWindow, setFlagDeleteWindow] = useState(false);
    //const [flagWarningWindow, setFlagWarningWindow] = useState(false);
    const flagDeleteWindow = useSelector(state => state.stateFlagsEltypeClass.confirmDeleting);
    const flagWarningWindow = useSelector(state => state.stateFlagsEltypeClass.warning);

    /*определение по какой строке таблицы был клик */
    const handlerTableRowUID = (event, data)=>{
        event.preventDefault();
        
        stateTable.map( (obj, index) => {
            if (obj.uid === data){
                console.log("index ", index);
                dispatch(saveDataStore(obj));
            }
        })
    }
    const hendlerOpenWindow = (nameWindow, typeRequest) => {
        dispatch(saveTypeRequest(typeRequest));
        dispatch(saveNameWindow(nameWindow));
    }
    const hendlerDelete = () => {

        if (dataDelete.uid === undefined || dataDelete.uid === null || dataDelete.uid === "") {
            openWindowConfirmDeleting(false);
            openWindowWarning(true);
        } else {
            openWindowConfirmDeleting(true);
            openWindowWarning(false);
        }
    }


 useEffect(() => {
     console.log(' useEffect GET ');
     dispatch(startGetRequestServer());
    }, [])


    if (stateLoading) {
        return <div>ЖДИТЕ, ИДЕТ ЗАГРУЗКА</div>
    } 
        return (
            <div>
                <HeaderDictionary
                    classname=""
                    nameHeader="Вывод данных Eltype Class"
                />

                <ConsoleButton
                    classname=""

                    name1="Обновить"
                    title1="Обновить"
                    onclick1={() => { dispatch(startGetRequestServer()) }}


                    name2="Добавить"
                    title2="Добавить"
                    onclick2={() => hendlerOpenWindow("Новое значение", "post")}
                    linkWindowEdit='/EditWindowEltypeClass'

                    name3="Внести изменения"
                    title3="Внести изменения"
                    onclick3={() => hendlerOpenWindow("Изменить значение", "put")}

                    name4="Удалить"
                    title4="Удалить"
                    onclick4={hendlerDelete}

                    name5="Только активные"
                    title5="Только активные"
                    onclick5={() => console.log('click')}



                    type="button"
                    tabindex="-1"

                />

                <TableList
                    columnName={Object.keys(stateTable[0])}
                    dataTable={stateTable}
                    onclick={(event, data) => handlerTableRowUID(event, data)}
                />

                {
                    flagDeleteWindow === false ?
                        null
                        : <WindowConfirmDeleting
                            onclickYesButton={() => console.log('onclickYesButton')}
                            onclickNoButton={() => console.log('onclickNoButton')}
                            classButtonX=''
                            dataDelete={dataDelete.name}
                            />
                }
                {
                    flagWarningWindow === false ?
                        null
                        : <WarningValueNotSelected
                                    classnameButtonWarningHelp=""
                                    classnameButtonWarning=""
                                    onclickWarning={() => console.log('onclickWarning')}
                            />
                }

            </div>
        )

}

export default connect()(EltypeClass)
