import React from 'react';
import { sortBase } from '../../functions/Sort';

function InputDatalist(props) {

   const arrValue = sortBase(props.sortKey, props.arrValue); // ��������� ������ ;
    
    return(
        <div className={props.classInputDatalis}>
        
            <label 
                    htmlFor={props.id}  
            >
                {props.label}
            </label>
        
                <input
                        id={props.id}
                        list={props.listId}
                        type='text'
                        placeholder={props.placeholder}
                        onChange={(event) => props.onchange(event)}

                        tabIndex={props.tabindex}
                        autoComplete={props.autocomplete}
                />

                <datalist id={props.listId} >
                    {
                        arrValue.map((data, index) => {
                            return <option key={index} value={data.name} ></option>
                        })
                    }
                </datalist>
            
        </div>
    )
}

export default InputDatalist
