import {
    SHOW_LOADER,
    STATUS_CONFIRM_DELETING,
    STATUS_OPEN_EDIT_WINDOW,
    STATUS_WARNING
                    } from "../types";

export const loadingShow = (data) => {

    return {
        type: SHOW_LOADER,
        payload: data
    }
}
export const openWindowConfirmDeleting = (data) => {

    return {
        type: STATUS_CONFIRM_DELETING,
        payload: data
    }
}
export const openEditWindow = (data) => {

    return {
        type: STATUS_OPEN_EDIT_WINDOW,
        payload: data
    }
}
export const openWindowWarning = (data) => {

    return {
        type: STATUS_WARNING,
        payload: data
    }
}
