import {
    EMPLOY_DESCRIPTION_ALL
                    } from "./types";

const initialState = {
                useDescriptionAll: true

            };


const stateFlagsAll = (state = initialState, action) => {

    switch (action.type) {

        case EMPLOY_DESCRIPTION_ALL:
            return {
                ...state,
                useDescriptionAll: action.payload
            }
 
        default:
            return state;
    }
}
export default stateFlagsAll
