import {CREATE_DATA} from "../types"

const initialState ={
    flag: false,
    stateData: {
                uid: 0,
                name: "",
                comment: "",
                classViewProperty: "",
                dbTable: ""
            },
    nameWindow: "Новые данные",
    labels: [
                'uid',
                'Наименование',
                'Примечания',
                'classViewProperty',
                'dbTable'
            ],
    placeholders: [
                'uid',
                'Введите полное наименование',
                'Дополнения и комментарии',
                'classViewProperty',
                'dbTable'
            ],
    typeData: [
                "number",
                "text",
                "text",
                "text",
                "text"
            ]
                    };

const WindowCreateNewData = (state = initialState, action) => {
    
    switch (action.type){
            
        case CREATE_DATA:
            return {
                ...state,
                stateData: action.payload
            }

        case FLAG_CHANGE:
            console.log ('FLAG_CHANGE', !state.flag)
            return {
                ...state,
                flag: !state.flag
            }
            
        default: 
            return state;
    }
}
export default WindowCreateNewData
