import { SAVE_DATA_AUTH } from "./types";

const initialState = {
                        uid: '',
                        login: '',
                        name: '',
                        password: ''
            };


const stateAuthorization = (state = initialState, action) => {

    switch (action.type) {

        case SAVE_DATA_AUTH:
            console.log('1111111')
            return action.payload

        default:
            return state;
    }
}
export default stateAuthorization
