import React, {useEffect, useRef } from 'react';
import { connect, useDispatch, useSelector} from "react-redux";

import EditWindow from '../../components/EditWindow/EditWindow'

function ContEditWindow (props){
    
    const stateTable = useSelector(state => state.stateTable);
    const labels = Object.keys(stateTable[0]);
    const [x1, x2, x3, x4, x5] = [...labels];
    const idName = "WindowNewData";
    
    const refInput1 = React.createRef();
    
    useEffect(()=>{
        
        refInput1.current.focus();
	       return () =>{};
    }, [])
    
    const handlerClick = ()=>{
        console.log('handlerClick');
    }
    
    return(
        <EditWindow 
                    nameButtonSave = "Сохранить"
                    nameButtonClose = "Отмена"
                    onclick1 = {handlerClick}
                    onclick2 = {handlerClick}
                    classWrapper = ""
                    classButtonWrapper = ""
                    classButton = ""
                    classButtonX = ""
        
                    id1 = {x1 + idName}
                    ref = {refInput1}
                    label1 = {x1}
                    type1 = "number"
                    placeholder1 = {x1}
                    value1 = ""
                    onchange1 = {handlerClick}
                    tabIndex1 = '1'
                    autofocus1 = "autofocus"
        
                    id2 = {x2 + idName}
                    label2 = "Наименование"
                    type2 = "text"
                    placeholder2 = "Введите наименование"
                    value2 = ""
                    onchange2 = {handlerClick}
                    tabIndex2 = '2'
        
                    id3 = {x3 + idName}
                    label3 = "Комментарии"
                    cols = "20"
                    rows = "5"
                    placeholder3 = "Оставьте комментарии"
                    value3 = ""
                    onchange3 = {handlerClick}
                    tabIndex3 = '3'
        
                    id4 = {x4 + idName}
                    label4 = "Класс"
                    type4 = "text"
                    placeholder4 = "Укажите класс"
                    value4 = ""
                    onchange4 = {handlerClick}
                    tabIndex4 = '4'
                    autofocus4 = ""
        
                    id5 = {x5 + idName}
                    label5 = {x5}
                    type5 = "text"
                    placeholder5 = {x5}
                    value5 = ""
                    onchange5 = {handlerClick}
                    tabIndex5 = '5'
                    autofocus5 = ""
        
                    classNameLabel = ''
                    classNameInput = ''
                    classNameTextarea = ''
            />
    )
}

export default connect()(ContEditWindow)
