import React from 'react';
import { connect } from 'react-redux';

import InputAuth from '../../UserInterface/InputAuth/InputAuth';
import { linkPersonalize } from '../Navigation/Main';
import ButtonLink from '../../UserInterface/Buttons/Button/ButtonLink';

function mapStateToProps(state) {
    return {
        stateLogin: state.stateAuthorization.user.login,
        statePassword: state.stateAuthorization.user.password
    }
} 

class LoginAuthChange extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            passwordOld: '',
            login: '',
            passwordNewFirst: '',
            passwordNewSecond: '',
            helloName: '',
            flagPasswordOld: false,
            flagPasswordNewFirst: false,
            flagPasswordNewSecond: false
        };
        this.dataInputAuth = {

            passwordOld: {
                type: 'password',
                placeholder: 'действующий пароль',
                autofocus: true,
                onblur: this.onblur(this.statePassword, this.state.passwordOld, this.state.flagPasswordOld),
                onfocus: this.onfocus(this.state.flagPasswordOld)
            },
            passwordNewFirst: {
                type: 'password',
                placeholder: 'новый пароль',
                autofocus: false,
                onblur: this.onblurPasswordNewFirst(this.state.passwordNewFirst, this.state.flagPasswordNewFirst),
                onfocus: this.onfocus(this.state.flagPasswordNewFirst)
            },
            passwordNewSecond: {
                type: 'password',
                placeholder: 'подтвердите пароль',
                autofocus: false,
                onblur: this.onblur(this.state.passwordNewFirst, this.state.passwordNewSecond, this.state.flagPasswordNewSecond),
                onfocus: this.onfocus(this.state.flagPasswordNewSecond)
            }
        };
        
    }

    helloLogin = () => {
        let loginLocalStorage = localStorage.getItem('login');
        if (localStorage.getItem('login') === null) { loginLocalStorage = '' };
        this.setState({
            login: loginLocalStorage,
            helloName: localStorage.getItem('nameAuth')
        })
    }

    onchange = (event, nameInput) => {
        this.setState({
            [nameInput]: event.target.value
        });
    }

    onblur = (dataOld, dataNew, keyData) => {
        if (dataOld !== dataNew) {
        this.setState({
                    [keyData]: ![keyData]
                })
        } 
    }

    onblurPasswordNewFirst = (data, keyData) => {
        if (data === '' || data === ' ' || data === null || data === undefined) {
            this.setState({
                [keyData]: ![keyData]
            })
        }
    }

    onfocus = (keyData) => {
        this.setState({
            [keyData]: false
        })
    }
     
    onclick = nameInput => {
        this.setState({
            [nameInput]: ''
        });
    }

    hendlerSave = () => {
        console.log('сохранить новый пароль  ', this.state);

    }


    render() {

        return (
            <div>
                <h2> Изменить пароль входа в систему </h2>
                <h3> Для login {this.props.stateLogin}</h3>

                {
                    Object.keys(this.dataInputAuth).map((keyState, index) => {
                        return <InputAuth
                                        value={this.state[keyState]}
                                        key = { index }
                                        id={keyState}
                                        tabindex={index}
                                        {...this.dataInputAuth[keyState]}
                                        onchange={this.onchange}
                                        onclick={this.onclick}
                                    />
                    })
                }


                <ButtonLink
                    onclick={this.hendlerSave}
                    to={linkPersonalize}
                    name="Сохранить новый пароль"
                    icon="fa fa-floppy-o"
                />

            </div>

            )
    }
    
}

export default connect(mapStateToProps)(LoginAuthChange)
