import React, { useEffect, useState} from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import ConsoleButton from '../../components/ConsoleButton/ConsoleButton';
import TableList from '../../components/Table/TableListEltypeClass';
import HeaderDictionary from '../../components/Header/HeaderDictionary';
import ContextMenuWindow from '../../components/ContextMenuWindow/ContextMenuWindow';
import AwaitLoading from '../../UserInterface/AwaitLoading/AwaitLoading';

import {
        startGetRequestServerEltypeClass,
        saveDataStore,
        saveTypeRequest,
        saveNameWindow
 } from '../../store/EltypeClass/actions/actionEltypeClass';

import {
        changeCountRowTable,
        getCounterPagesTable,
        changeNumberList,
        getDataPageRowTable,
        changeActivArrNumberPagesFooterTable
} from '../../store/EltypeClass/actions/actionsFlagsEltypeClass';

import { addDescription } from "../../store/Description/actionsDescription";

import { tablebodyBackgroundClear } from '../../functions/tablebodyBackgroundClear';

import { changeAdressOpenTable } from '../../store/EltypeClass/actions/actionsLinksEltypeClass';

import { addDataCompareArr } from '../../store/EltypeClass/actions/actionsCompareEltypeClass';
import withSearch from '../../hoc/withSearch';


function EltypeClass(props) {

    
    const dispatch = useDispatch();

    const stateLoading = useSelector(state => state.stateFlagsEltypeClass.loading);

    const stateTable = useSelector(state => state.stateTableEltypeClass); //ВСЯ база

    const dataTable = useSelector(state => state.stateFlagsEltypeClass.dataListTable); // отображаемый лист

    const counterPages = useSelector(state => state.stateFlagsEltypeClass.counterPagesTable); // общее количество листов в таблице

    const activArrNumberPages = useSelector(state => state.stateFlagsEltypeClass.activArrNumberPagesFooterTable); //предлагаемый на выбор массив из 3х кнопок в консоли переключения отображаемых листов таблицы

    const countRow = useSelector(state => state.stateFlagsEltypeClass.countRowTable); // количество строк на отображаемой странице

    const numberList = useSelector(state => state.stateFlagsEltypeClass.numberList); //номер отображаемого листа

    const focusData = useSelector(state => state.createDataEltypeClass.stateData); // фокус. запомнить данные выбранной строки

    const compareArr = useSelector(state => state.compareDataEltypeClass); // массив строк, избранных для сравнения

    const links = useSelector(state => state.stateLinksEltypeClass); //список ссылок для переходов между окнами

    const flagEmployDescription = useSelector(state => state.stateFlagsAll.employDescriptionAll) // разрешен ли пользователем показ подсказок

    const arrDescription = useSelector(state => state.stateDescription); // массив подсказок из store (пока без кнопок консоли, заполнится после рединга)

    const focusNull = () => {
        tablebodyBackgroundClear();
        dispatch(saveDataStore({
                                uid: "",
                                name: "",
                                isDeleted: false,
                                isTemplate: false
                            }));
    }

    //правая кнопка мыши открывает контестное меню
    const [flagOpenContextMenuWindow, setFlagOpenContextMenuWindow] = useState({
                                                                                flag: false,
                                                                                clientX: '',
                                                                                clientY: ''
                                                                            });
    const hendlerOpenWindow = (nameWindow, typeRequest) => {
        dispatch(saveTypeRequest(typeRequest));
        dispatch(saveNameWindow(nameWindow));
    }

    const [flagCountRow, setFlagCoutnRow] = useState(false);  // флаг инпута в футере для изменения к-ва строк на листе

    const hendlerInputOpen = () => {
        setFlagCoutnRow(true);
    }
    const hendlerChangeCountRow = (event) => {
            event.preventDefault();
            dispatch(changeCountRowTable(event.target.value));
            dispatch(changeNumberList(1)); // при изменении к-ва строк на странице всегда возвращаемся на страницу №1, если это не надо, убрать эту строку, но может появляться путой лист
            if (numberList !== 1) {
                      focusNull();  // переходим на страницу 1, фокус теряется, поэтому в хранилище обнуляем данные
                  };
    }
    const hendlerInputSave = () => {
        setFlagCoutnRow(false);
        dispatch(getCounterPagesTable(stateTable, countRow));
        dispatch(getDataPageRowTable(stateTable, countRow, numberList));
    }

    //после выбора страницы приходит ее номер, в хранилище собираем новую страницу, в хранилище сохраняем номер страницы
    const hendlerOpenPagesTable = (num) => {

        dispatch(getDataPageRowTable(stateTable, countRow, num));
        dispatch(changeNumberList(num));
        focusNull(); // при переходе на другую страницу фокус теряется, поэтому в хранилище обнуляем данные
        dispatch(changeActivArrNumberPagesFooterTable(num, counterPages)); //измениям консоль выбора активной страницы в футере
    }
    const hendlerChangeButtonConsole = (num) => {
        dispatch(changeActivArrNumberPagesFooterTable(num, counterPages, activArrNumberPages)); //переключение стрелками консоли выбора активной страницы в футере
    }

    const hendlerAddCompareData = () => {
        dispatch(addDataCompareArr(focusData, compareArr));
    }

    const hendlerOpenCompareWindow = () => {
        dispatch(addDataCompareArr(focusData, compareArr)); //добавить выделенную строку в сравнение
        focusNull();
        dispatch(changeAdressOpenTable(links.linkCompare));
    }


    const HeaderDictionaryWithSearch = withSearch(HeaderDictionary, stateTable);

  
   const stateConsoleButton = [
        {
            text: "Обновить",
            name: 'update',
            icon: "fa fa-download fa-lg",
            onclick: () => { dispatch(startGetRequestServerEltypeClass()) },
            link: links.linkBase
        },
        {
            text: "Добавить новые данные",
            name: 'add',
            icon: "fa fa-plus fa-lg",
            onclick: () => hendlerOpenWindow("Новые данные", "post"),
            link: links.linkEditWindow
        },
        {
            text: "Внести изменения",
            name: 'edit',
            icon: "fa fa-pencil-square-o fa-lg",
            onclick: () => hendlerOpenWindow("Внести изменения", "put"),
            link: links.linkEditWindow
        },
        {
            text: "Удалить",
            name: 'delete',
            icon: "fa fa-times fa-lg",
            onclick: () => { },
            link: links.linkWindowConfirmDeleting
        },
        {
            text: "Сравнить",
            name: 'compare',
            icon: "fa fa-balance-scale",
            onclick: hendlerOpenCompareWindow,
            link: links.linkCompare
        },
        {
            text: "Добавить к сравнению",
            name: 'addCompare',
            icon: "fa fa-filter fa-lg",
            onclick: () => hendlerAddCompareData(),
            link: links.linkBase
        }
    ];  

 useEffect(() => {
  //   dispatch(startGetRequestServerEltypeClass());

     document.oncontextmenu = function (event) {
         if (event.defaultPrevented) return;
         event.preventDefault();
         if (event.target.tagName === 'TD') {
             setFlagOpenContextMenuWindow({
                 flag: true,
                 clientX: event.clientX,
                 clientY: event.clientY
             });
         };

     };

     const SearchBaseName = document.getElementById('SearchBaseName');
     document.onclick = function (event) {
         event.preventDefault();
         setFlagOpenContextMenuWindow({
             flag: false,
             clientX: '',
             clientY: ''
         });
         if (event.target.tagName !== "INPUT") {

             SearchBaseName.value = '';
         }
     };
     document.onscroll = function () {
         setFlagOpenContextMenuWindow({
             flag: false,
             clientX: '',
             clientY: ''
         });
     };

     //добавляем подсказки на кнопки консоли
     if (flagEmployDescription === true) {
         let aboutDescription = {};
         stateConsoleButton.map((obj) => {
             aboutDescription[obj.name] = [obj.text];
         });
         dispatch(addDescription(aboutDescription, arrDescription));
     };

    }, [])



    return (
        <div className="DataBase__container">
 
                <div className="DataBase__header__container">
                    <ConsoleButton
                            classnameWrapper="DataBase__header__console"
                            stateConsoleButton={stateConsoleButton}
                            classnameDescription='DescriptionButton'
                            tabindex="-1"
                            />

                <HeaderDictionaryWithSearch
                            classname="DataBase__header__header"
                            nameHeader="Классы для Типов связей и Типов сущностей"
                            dataBase={stateTable}   
                        />
                </div>
            {
                stateLoading ?
                    <AwaitLoading
                        classname="Loading"
                    />
                    : <TableList
                                classname="DataBase__table__container"
                                classnameConsoleCountRow='ConsoleCountRow'
                                classnameButtonConsoleCountPow="PersonalizeButton"
                                stateTable={stateTable}
                                dataTable={dataTable}

                                onclickInputOpen={hendlerInputOpen}
                                onclickInputSave={hendlerInputSave}
                                onclickOpenPagesTable={(numButton) => hendlerOpenPagesTable(numButton)}
                                onclickChangeButtonConsole={hendlerChangeButtonConsole}
                                flagCountRow={flagCountRow}
                                onchangeCountRow={(event) => hendlerChangeCountRow(event)}
                                CountRow={countRow}
                                counterPages={counterPages}
                                activArrNumberPages={activArrNumberPages}
                                focusUid={focusData.uid}
                                numberActivList={numberList}
                        /> 
                }
                {
                    flagOpenContextMenuWindow.flag ?
                        <ContextMenuWindow
                            stateConsoleButton={stateConsoleButton}
                            classname="ContextMenuWindow"
                            clientX={flagOpenContextMenuWindow.clientX}
                            clientY={flagOpenContextMenuWindow.clientY}
                        />
                        : null
                }

        </div>
    )

}

export default connect()(EltypeClass)
