import React from 'react';
import { connect } from "react-redux";
import { rowFocus_Coffee } from '../styles/const_color';

//import { sortingTable } from '../store/EltypeClass/actions/actionEltypeClass';
//import { getDataPageRowTable, sortingList } from '../store/EltypeClass/actions/actionsFlagsEltypeClass';
//import { sortingCompareArr } from '../store/EltypeClass/actions/actionsCompareEltypeClass';

//import { focusRowColorBackground } from '../functions/focusRowColorBackground';

const mapStateToProps = (state) => {
    return {
//        activList: state.stateFlagsEltypeClass.dataListTable, // отображаемый лист
//        compareArr: state.compareDataEltypeClass, // массив строк, избранных для сравнения
//        focusData: state.createDataEltypeClass.stateData,
        countRow: state.stateFlagsEltypeClass.countRowTable, // количество строк на отображаемой странице
//        numberList: state.stateFlagsEltypeClass.numberList //номер отображаемого листа
        }
    }
const mapDispatchToProps = {
    saveDataStore,
    changeNumberList,

        //sortingTable,
        //getDataPageRowTable,
        //sortingList,
        //sortingCompareArr
}

const withSearch = (Component, data) => {

 
    class ComponentWithSort extends React.Component {
        constructor(props) {
            super(props);
            this.num = 0; // номер выбранного объекта в массиве
            this.numRowOnPage = 0; // номер строки объекта на отображаемой странице (в дальнейшем выделим цветом)

        }
        hendlerSearch = (event) => {
            event.preventDefault();
            if (event.target.value === '') { return }; //если пусто, выходим

            data.map((obj, index) => {

                if (obj.name === event.target.value) {
                    this.num = index + 1;
                    this.numRowOnPage = index + 1;
                    dispatch(saveDataStore(obj)); // сохранить фокус в хранилище
                }
            });
            //поиск номера страницы
            let i = this.props.countRow;
            let numList = 1;
            if (this.num === undefined) { return }; //если пусто, выходим

            while (i < this.num) {
                i = i + this.props.countRow;
                this.numRowOnPage = this.numRowOnPage - this.props.countRow;
                numList++;
            }

            dispatch(changeNumberList(numList)); //сохраняем номер
            dispatch(getDataPageRowTable(data, this.props.countRow, numList));// загружаем выбранный лист

            const tablebody = document.querySelector('tbody');

            for (let child of tablebody.children) {
                child.style.background = allRowColorBackground;//убираем цвет ранее выбранной строки
            };

            tablebody.children[this.numRowOnPage - 1].style.background = rowFocus_Coffee; // выделяем цветом
            tablebody.children[this.numRowOnPage - 1].children[1].focus();
        }

        componentDidMount() {

        }

        render() {
            let props = {
                ...this.props,
                onchange: this.hendlerSearch
            }
            return (
                <Component {...props} />
                )
        }
    }
    return connect(mapStateToProps, mapDispatchToProps)(ComponentWithSort)
}
export default withSearch
















