import React, { useState} from 'react';
import Select from '../../UserInterface/Select/Select';

function Personalize(props) {

    const stylesList = [
        {
            value: "STYLE_1",
            text: "Coffee"},
        {
            value: "STYLE_2",
            text:"GreyBlue"}
    ];

    const [styleValue, setStyleValue] = useState("STYLE_1");

    const hendlerSelect = (event) => {
        event.preventDefault();
        if (event.target.tagName != 'SELECT') return;
        setStyleValue(event.target.value);
        console.log('Select ', event.target.value);
    }

    return (
            <div className="Personalize">
                <div>
                    <Select
                        id="styleSelect"
                        onchange={hendlerSelect}
                        value={styleValue}
                        tabindex='1'
                        arrOptionSelect={stylesList}
                     />
                </div>
            </div>
    )
}

export default Personalize
