import React from 'react';

import ButtonSmall from '../../UserInterface/Buttons/Button/ButtonSmall';
import Button from '../../UserInterface/Buttons/Button/Button';
import Input from '../../UserInterface/Input/Input';


function ConsoleCountRow(props) {

    const counterPages = [];

    for (let i = 0; i < props.counterPages; i++) {
        counterPages[i] = i + 1;
    };

    return (

        <div className={props.classname}>

            <div>
                Открыта страница : <span>{props.numberActivList}</span>
            </div>

            <div>
                {
                    counterPages.length > 3 ?
                        <>
                            <Button
                                onclick={props.onclick}
                                tabindex='-1'

                            />
                            <Button
                                onclick={props.onclick}
                                tabindex='-1'

                            />
                        </>
                        : <p>ghbdtn</p>
                }
                {
                    counterPages.map((num, index) => {
                        if (num == props.numberActivList || num == (props.numberActivList - 1) || num == (props.numberActivList + 1)){
                                return <Button
                                            onclick={props.onclick}
                                            tabindex='-1'
                                            name={num}
                                            data={num}
                                            key={index}
                                        />
                        } else {
                                 return null
                        }

                    })
                }
            </div>
            
               
            <div>
                {
                    props.flagCountRow ?
                        <Input
                            id="inputCountRow"
                            type="number"
                            label="Строк на странице : "
                            onchange={props.onchange}
                            value={props.CountRow}
                            tabindex="-1"
                        />
                        : <div> Строк на странице : <span> {props.CountRow} </span></div>
                }
            </div>
            {
                props.flagCountRow ?
                    <ButtonSmall
                        className={props.classnameButtonConsoleCountRow}
                        onclick={props.onclickInputSave}
                        tabindex='-1'
                        icon="fa fa-floppy-o"
                    />
                    :<ButtonSmall
                        className={props.classnameButtonConsoleCountRow}
                        onclick={props.onclickInputOpen}
                        tabindex='-1'
                        icon="fa fa-pencil"
                    />
             }

           

            
        </div>

    )
}

export default ConsoleCountRow
