import React from 'react';
import InputAuth from '../../UserInterface/InputAuth/InputAuth';



class Authorization extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            surname: '',
            name: '',
            secondName: '',
            login: '',
            password: ''
        };
        this.dataInputAuth = {
            surname: {
                type: 'text',
                placeholder: 'фамилия',
                autofocus: false
            },
            name: {
                type: 'text',
                placeholder: 'имя',
                autofocus: true
            },
            secondName: {
                type: 'text',
                placeholder: 'отчество',
                autofocus: false
            },
            login: {
                type: 'text',
                placeholder: 'login',
                autofocus: false
            },
            password: {
                type: 'text',
                placeholder: 'пароль',
                autofocus: false
            }
        };
        
    }

    onchange = () => {

    }
     
    onclick = (nameInput) => {
        this.setState = ( prevState => ({
            ...prevState,
            [nameInput]: ''
        }))
    }

    hendlerSave = () => {
        console.log(' сохранить карточку на сервер ', this.state)
    }


    render(){
        return (
            <div>
                {
                    Object.keys(this.dataInputAuth).map((keyState, index) => {
                        return <InputAuth
                                        value={this.state[keyState]}
                                        key = { index }
                                        id={keyState}
                                        tabindex={index}
                                        {...this.dataInputAuth[keyState]}
                                        onchange={this.onchange}
                                        onclick={this.onclick}
                                    />
                    })
                }
               
                <button
                    onClick={this.hendlerSave}
                >
                    Сохранить
                </button>

            </div>

            )
    }
    
}

export default Authorization
