import React, { useState } from 'react';


const hoverDescription = (Component) => {

    class ButtonHOC extends React.Component {

        render() {

            mouseOver = () => console.log('OPEN')
            mouseOut = () => console.log('CLOSE')

            return(

                <div onMouseOver={this.mouseOver} onMouseOut={this.mouseOut}>
                    <Component {...props} />
                </div>
                )
        }
    }

    return ButtonHOC;
}
export default hoverDescription
