import { takeEvery, select, put, call } from 'redux-saga/effects';
import {getRequestServer} from '../../store/serverViewType/actions/actionViewType';
import {url_POST} from "../../constans/url"
import {START_POST_REQUEST} from '../../store/windowCreateNewData/types';
//import {newDataFromServer} from '../../containers/EditWindow/ContEditWindow'



const url = "http://localhost:5000/api/ViewType/add"
//export function fetchData(){
//    return fetch("http://localhost:5000/api/ViewType")
//        .then(response => response.json())
//}

export function fetchData(bodySendServer){
    console.log('Это fetch POST')
//    const newDataFromStore = useSelector(state => state.WindowCreateNewData.stateData);
    return
        fetch( url_POST,{
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                  },
                body: JSON.stringify(bodySendServer)
                })
        .then(response => response.json())
        .catch(err => console.log(err))
}
export function RequestData(data){
    console.log( 'Это RequestData', data);
    getRequestServer(data);
}

export function* workerStart_POST_RequestServer(){
     
    console.log('workerStart_POST_RequestServer ');
    
    try {
        
//        const data = yield call(fetchData);
        
        const state = yield select();
        
        console.log ("Результат запроса state", state.WindowCreateNewData.stateData);
        
        let stateSendServer = state.WindowCreateNewData.stateData
        
        const data = yield call(fetchData(stateSendServer));
        
        console.log ("Результ POST ", data);
        
//        yield put(RequestData(data));
        
    } catch (error) {
        alert('workerStart_POST_RequestServer error ', error);
    }
 
}

export function* watchStart_POST_RequestServer(){
    
    yield takeEvery(START_POST_REQUEST, workerStart_POST_RequestServer)
}
