import React from 'react';
import InputAuth from '../../UserInterface/InputAuth/InputAuth';
import { linkHome } from '../Navigation/Main';
import ButtonLink from '../../UserInterface/Buttons/Button/ButtonLink';


class Authorization extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            surname: '',
            name: '',
            secondName: '',
            login: '',
            password: ''
        };
        this.dataInputAuth = {
            surname: {
                type: 'text',
                placeholder: 'фамилия',
                autofocus: true
            },
            name: {
                type: 'text',
                placeholder: 'имя',
                autofocus: false
            },
            secondName: {
                type: 'text',
                placeholder: 'отчество',
                autofocus: false
            },
            login: {
                type: 'text',
                placeholder: 'login',
                autofocus: false
            },
            password: {
                type: 'password',
                placeholder: 'пароль',
                autofocus: false
            }
        };
        
    }

    onchange = (event, nameInput) => {
        this.setState({
            ...this.state,
            [nameInput]: event.target.value
        });
    }
     
    onclick = nameInput => {
        this.setState({
            ...this.state,
            [nameInput]: ''
        });
    }

    hendlerSave = () => {
        console.log(' сохранить карточку на сервер ', this.state);
        localStorage.setItem('login', this.state.login);
        let nameLogin = this.state.name + " " + this.state.secondName;
        localStorage.setItem('nameAuth', nameLogin);
    }
    hendlerClearAll = () => {
        this.setState({
            surname: '',
            name: '',
            secondName: '',
            login: '',
            password: ''
        });
    }


    render(){
        return (
            <div>
                <h2>Для начала работы пройдите регистрацию :</h2>

                {
                    Object.keys(this.dataInputAuth).map((keyState, index) => {
                        return <InputAuth
                                        value={this.state[keyState]}
                                        key = { index }
                                        id={keyState}
                                        tabindex={index}
                                        {...this.dataInputAuth[keyState]}
                                        onchange={this.onchange}
                                        onclick={this.onclick}
                                    />
                    })
                }

                <ButtonLink
                    onclick={this.hendlerSave}
                    to={linkHome}
                    name="Сохранить"
                    icon="fa fa-user-plus"
                />
                <button
                    onClick={this.hendlerClearAll}
                >
                    <span className="fa fa-times" aria-hidden="true"></span>
                </button>

            </div>

            )
    }
    
}

export default Authorization
