import React from 'react';


const hoverDescription = (Component, propIsOpen, flagActiv) => {

    return class extends React.Component {

        state = { isOpen: false };
        mouseOver = () => {
            if (props.flagHaveDescription == true) {
                this.setState({ isOpen: true })
            } else {
                this.setState({ isOpen: false })
            }

        };
        mouseOut = () => this.setState({ isOpen: false });

        render() {

            const props = {
                [propIsOpen]: this.state.isOpen,
                ...this.props,
            }

            return(

                <div
                    onMouseOver={this.mouseOver}
                    onMouseOut={this.mouseOut}
                    onClick={this.mouseOut}
                >
                    <Component {...props} />
                </div>
                )
        }
    }

}
export default hoverDescription
