import React, { useState } from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import ButtonLinkSmallNotMouse from '../../UserInterface/Buttons/Button/ButtonLinkSmallNotMouse';
import ConsoleButtonCompare from '../../components/ConsoleButton/ConsoleButtonCompare';
import HeaderDictionary from '../../components/Header/HeaderDictionary';
import CompareTableEltypeClass from '../../components/Table/CompareTableEltypeClass';

import {
    saveDataStore,
    saveTypeRequest,
    saveNameWindow,
} from '../../store/EltypeClass/actions/actionEltypeClass';

import { changeAdressOpenTable } from '../../store/EltypeClass/actions/actionsLinksEltypeClass';

import {
    clearCompareArr,
    deleteDataCompareArr
} from '../../store/EltypeClass/actions/actionsСompareEltypeClass';


import { tablebodyBackground_Coffee, rowFocus_Coffee } from '../../styles/const_color';

function CompareEltypeClass(props) {


    const dispatch = useDispatch();

    const stateTable = useSelector(state => state.stateTableEltypeClass); //ВСЯ база

    const compareArr = useSelector(state => state.compareDataEltypeClass); // массив строк, избранных для сравнения

    const focusData = useSelector(state => state.createDataEltypeClass.stateData); //хранение данных выбранной строки

    const links = useSelector(state => state.stateLinksEltypeClass); //список ссылок для переходов между окнами

    const tablebodyBackgroundClear = () => {
        let tablebody = document.getElementById('tablebody');
        for (let child of tablebody.children) {
            child.style.background = tablebodyBackground_Coffee;//убираем цвет ранее выбранной строки
        };
    }

    const handlerTableRowUID = (event, data) => { //фокус на строку
        event.preventDefault();

        let tr; //строка выбранной ячейки
        if (event.target.parentElement.tagName == 'TR') {
            tr = event.target.parentElement; 

            stateTable.map((obj) => {
                if (obj.uid === data) {
                    dispatch(saveDataStore(obj)); // сохранить в хранилище для изменений
                }
            })
        } else if (event.target.parentElement.tagName == 'TD') {
            tr = event.target.parentElement.parentElement;

            stateTable.map((obj) => {
                if (obj.uid === data) {
                    dispatch(saveDataStore(obj)); // сохранить в хранилище для изменений
                }
            })
        } else if (event.target.parentElement.tagName == 'DIV') {
            tr = event.target.parentElement.parentElement.parentElement; //просто выделить, удаление идет через кнопку
        };
        tablebodyBackgroundClear(); // очищаем поле
        tr.style.background = rowFocus_Coffee; // выделяем цветом строчку
    }

    const hendlerOpenWindow = (nameWindow, typeRequest) => {
        dispatch(saveTypeRequest(typeRequest));
        dispatch(saveNameWindow(nameWindow));
    }

    const hendlerClearCompareArr = () => {
        dispatch(clearCompareArr());
        // изменяем ссылку перенаправления на активную таблицу для закрытия вспомогательных окон:
        dispatch(changeAdressOpenTable(links.linkBase));
    }

    const HendlerOnMouseover = () => {}
    const HendlerOnMouseLeave = () => {}
    
    const hendlerDelete = (row) => {
        dispatch(deleteDataCompareArr(row, compareArr)); // удалить из массива сравнений
    }

    const hendlerCloseWindow = () => {
            //закрываем окно и поэтому обнуляем хранение фокуса:
            dispatch(saveDataStore({
                                        uid: "",
                                        name: "",
                                        isDeleted: false,
                                        isTemplate: false
                                    })
                        );
            // изменяем ссылку перенаправления на активную таблицу для закрытия вспомогательных окон:
            dispatch(changeAdressOpenTable(links.linkBase));
    }

    const stateConsoleButton = [
  
        {
            text: "Внести изменения",
            icon: "fa fa-pencil-square-o fa-lg",
            onclick: () => hendlerOpenWindow("Внести изменения", "put"),
            link: links.linkEditWindow
        },
        {
            text: "Удалить",
            icon: "fa fa-times fa-lg",
            onclick: () => { },
            link: links.linkWindowConfirmDeleting
        },
        {
            text: "Очистить список сравнений",
            icon: "fa fa-minus-square-o",
            onclick: hendlerClearCompareArr,
            link: links.linkBase
        }
    ];


    return (

        <div className="DataBase__container">

            <div className="DataBase__header__container">

                <ConsoleButtonCompare
                    classnameWrapper="DataBase__header__console"

                    stateConsoleButton={stateConsoleButton}

                    onmouseover={HendlerOnMouseover}
                    onmouseleave={HendlerOnMouseLeave}

                />

                <HeaderDictionary
                    classname="DataBase__header__header"
                    nameHeader="Сравнить избранное: Классы для Типов связей и Типов сущностей"
                />

                <ButtonLinkSmallNotMouse
                    to={links.linkBase}
                    icon="fa fa-times"
                    onclick={hendlerCloseWindow}
                />
            </div>
            {
                (compareArr.length > 1) ?
                    <CompareTableEltypeClass
                        columnName={Object.keys(compareArr[0])}
                        dataTable={compareArr}
                        onclick={(event, data) => handlerTableRowUID(event, data)}
                        onclickDelete={hendlerDelete}
                    />
                    : <div>недостаточно данных</div>

            }

        </div>
    )
}

export default connect()(CompareEltypeClass)
