import {
    SHOW_LOADER
                    } from "../types";

export const loadingShow = (data) => {

    return {
        type: SHOW_LOADER,
        payload: data
    }
}

export const getCounterPages = (dataFromServer) => {

    return {
        type: GET_REQUEST,
        payload: dataFromServer
    }
}
