import {GET_REQUEST, POST_REQUEST} from "../types";

const initialState =[
          {}
        ]


const stateTable = (state = initialState, action) => {
    
    switch (action.type){
            
        case GET_REQUEST:
            return  action.payload
            
        case POST_REQUEST:
            return  [
                ...state,
                action.payload
            ]
 
        default: 
            return state;
    }
}
export default stateTable
