import {
    TYPE_REQUEST,
    NAME_WINDOW
} from "../types"

//"uid": "",
//"login": "",
//"name": "",
//"password": "",
//"isDeleted": false,
//"isSuperUser": false,
//"docInfo": [],
//"projectAuthorU": [],
//"projectUpdaterU": [],
//"userProjects": []

const initialState = {
    nameWindow: "",
    typeRequest: null,
    nameСolumnsTableList: {
        "login": false,
        "name": true,
        "password": true,
        "isDeleted": true,
        "isSuperUser": true
    },
    nameСolumnsCompare: {
        "login": true,
        "name": true,
        "password": true,
        "isDeleted": true,
        "isSuperUser": true
    },
    labels: [
        'login',
        'Ф.И.О.',
        'пароль',
        'isDeleted',
        'isSuperUser'
    ],
    placeholders: [
        'login',
        'Ф.И.О.',
        'пароль',
        'isDeleted',
        'isSuperUser'
    ],
    typeData: {
        "login": "text",
        "name": "text",
        "password": "text",
        "isDeleted": "checkbox",
        "isSuperUser": "checkbox"

    },
    typeInput: [
                "input",
                "input",
                "input",
                "checkbox",
                "checkbox"
            ]
};

const stateInputEditWindowUser = (state = initialState, action) => {
    
    switch (action.type){
            
   
        case TYPE_REQUEST:
            return {
                ...state,
                typeRequest: action.payload
            }
    
        case NAME_WINDOW:
            return {
                ...state,
                nameWindow: action.payload
            }

        default: 
            return state;
    }
}
export default stateInputEditWindowUser
