import React, { useEffect } from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import ConsoleButton from '../../components/ConsoleButton/ConsoleButton';
import TableList from '../../components/Table/TableListEltypeClass';
import HeaderDictionary from '../../components/Header/HeaderDictionary';
import AwaitLoading from '../../UserInterface/AwaitLoading/AwaitLoading';

import {
        startGetRequestServer,
        saveDataStore,
        saveTypeRequest,
        saveNameWindow
} from '../../store/EltypeClass/actions/actionEltypeClass';

import { linkEditWindowEltypeClass } from '../Navigation/Main';
import { linkWindowConfirmDeleting } from '../Navigation/Main';

import "../../styles/EltypeClass.scss";
import "../../styles/AwaitLoading.scss";

function EltypeClass(props) {

    const linkAddressWindowEdit = linkEditWindowEltypeClass;
    const linkAddressWindowDelete = linkWindowConfirmDeleting;
    
    const dispatch = useDispatch();

    const stateLoading = useSelector(state => state.stateFlagsEltypeClass.loading);

    const stateTable = useSelector(state => state.stateTableEltypeClass);

    const handlerTableRowUID = (event, data)=>{
        event.preventDefault();
        
        stateTable.map( (obj, index) => {
            if (obj.uid === data){
                console.log("index ", index);
                dispatch(saveDataStore(obj));
            }
        })
    }
    const hendlerOpenWindow = (nameWindow, typeRequest) => {
        dispatch(saveTypeRequest(typeRequest));
        dispatch(saveNameWindow(nameWindow));
    }

 useEffect(() => {
     dispatch(startGetRequestServer());
    }, [])

    const styles = { loading: "Loading" };

    if (stateLoading) {
        return <AwaitLoading
            classname={styles.loading}
                />
    } 
    return (
        <div className="BodyDataBase">
                <HeaderDictionary
                    classname=""
                    nameHeader="Р’С‹РІРѕРґ РґР°РЅРЅС‹С… Eltype Class"
                />

                <ConsoleButton
                    classname=""

                    linkWindowEdit={linkAddressWindowEdit}
                    linkWindowConfirmDeleting={linkAddressWindowDelete}

                    name1="РћР±РЅРѕРІРёС‚СЊ"
                    title1="РћР±РЅРѕРІРёС‚СЊ"
                    onclick1={() => { dispatch(startGetRequestServer()) }}

                    name2="Р”РѕР±Р°РІРёС‚СЊ"
                    title2="Р”РѕР±Р°РІРёС‚СЊ"
                    onclick2={() => hendlerOpenWindow("РќРѕРІРѕРµ Р·РЅР°С‡РµРЅРёРµ", "post")}

                    name3="Р’РЅРµСЃС‚Рё РёР·РјРµРЅРµРЅРёСЏ"
                    title3="Р’РЅРµСЃС‚Рё РёР·РјРµРЅРµРЅРёСЏ"
                    onclick3={() => hendlerOpenWindow("РР·РјРµРЅРёС‚СЊ Р·РЅР°С‡РµРЅРёРµ", "put")}

                    name4="РЈРґР°Р»РёС‚СЊ"
                    title4="РЈРґР°Р»РёС‚СЊ"
                    onclick4={() => console.log("button delete open window")}

                    name5="РўРѕР»СЊРєРѕ Р°РєС‚РёРІРЅС‹Рµ"
                    title5="РўРѕР»СЊРєРѕ Р°РєС‚РёРІРЅС‹Рµ"
                    onclick5={() => console.log('click')}

                    type="button"
                    tabindex="-1"
                />

                <TableList
                    columnName={Object.keys(stateTable[0])}
                    dataTable={stateTable}
                    onclick={(event, data) => handlerTableRowUID(event, data)}
                />

            </div>
        )

}

export default connect()(EltypeClass)
