import React from 'react';
import { connect, useSelector } from "react-redux";

import hoverDescription from '../../hoc/hoverDescription';
import ButtonLinkSmall from './Button/ButtonLink';
import NavigationLinkAbout from '../../UserInterface/Navigation/NavigationLinkAbout';
import { aboutLinks } from "../../constans/aboutLinks";

function ButtonNavigationSmall(props) {

    const flagHaveDescription = useSelector(state => state.stateFlagsAll.haveDescriptionAll);

    return (
        <>
            {
                props.isOpenDescription == true && flagHaveDescription == true ?

                    < NavigationLinkAbout
                        aboutLink={aboutLinks[props.name]}
                        classname="NavigationLinkAbout"
                    />
                    :null
            }
            <ButtonLinkSmall
                to={props.to}
                classname={props.classname}
                onclick={props.onclick}
                icon={props.icon}
            />

        </>
    )
}

export default connect()(hoverDescription(ButtonNavigationSmall, 'isOpenDescription'))
