import { takeEvery, select, put, call } from 'redux-saga/effects';
import { postRequestServer } from '../../store/serverViewType/actions/actionViewType';
import { loadingShow, loadingHide } from '../../store/serverViewType/actions/actionsFlags';
import fetchData from '../../functions/fetchData';
import { url_POST } from "../../constans/url";
import { START_POST_REQUEST } from '../../store/serverViewType/types';

export function* workerStart_POST_RequestServer(){
    
    const stateSendServer = yield select( state => state.WindowCreateNewData.stateData);
    
    try {
        yield put(loadingShow(true));

        const data = yield call(fetchData, url_POST, 'POST', stateSendServer);
        
        yield put(postRequestServer(data));

        yield put(loadingShow(false));
         
    } catch (error) {
       console.log('workerStartRequestServer error ', error);
    }
 
}

export function* watch_ViewType_POST_RequestServer(){
    
    yield takeEvery(START_POST_REQUEST, workerStart_POST_RequestServer)

}
