import { takeEvery, put, call } from 'redux-saga/effects';
import { getRequestServer } from '../../store/serverViewType/actions/actionViewType';
import { loadingShow } from '../../store/serverViewType/actions/actionsFlags';
import { START_GET_REQUEST } from '../../store/serverViewType/types';
import fetchData from '../../functions/fetchData';
import {url} from "../../constans/url"


export function* workerStart_GET_RequestServer(){
    
    try {
        yield put(loadingShow(true));

        const data = yield call(fetchData, url);

        yield put(getRequestServer(data));

        yield put(loadingShow(false));
  
    } catch (error) {
        console.log('workerStart_GET_RequestServer error ', error);
    }
}

export function* watch_ViewType_GET_RequestServer(){
    
    yield takeEvery(START_GET_REQUEST, workerStart_GET_RequestServer)
}
