import React, { useEffect } from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import ConsoleButton from '../../components/ConsoleButton/ConsoleButton';
import TableList from '../../components/Table/TableList';
import HeaderDictionary from '../../components/Header/HeaderDictionary';
import AwaitLoading from '../../UserInterface/AwaitLoading/AwaitLoading';
import {
        startGetRequestServer,
        saveDataStore,
        saveTypeRequest,
        saveNameWindow
} from '../../store/serverViewType/actions/actionViewType';
import { linkViewTypeEditWindow } from '../Navigation/Main';

import "../../styles/AwaitLoading.scss";

function ViewType(props) {

    const linkAddressWindowEdit = linkViewTypeEditWindow;
    const linkAddressWindowDelete = '';
    
    const dispatch = useDispatch();

    /* РѕРїРѕРІРµС‰РµРЅРёРµ РЅР° РїРµСЂРёРѕРґ РѕР¶РёРґР°РЅРёСЏ РѕС‚РІРµС‚Р° СЃРµСЂРІРµСЂР°*/
    const stateLoading = useSelector(state => state.stateFlags.loading);

    /*РґР»СЏ Р·Р°РїРѕР»РЅРµРЅРёСЏ С‚Р°Р±Р»РёС†С‹ */
    const stateTable = useSelector(state => state.stateTable);

    
    /*РѕРїСЂРµРґРµР»РµРЅРёРµ РїРѕ РєР°РєРѕР№ СЃС‚СЂРѕРєРµ С‚Р°Р±Р»РёС†С‹ Р±С‹Р» РєР»РёРє */
    const handlerTableRowUID = (event, data)=>{
        event.preventDefault();
        
        stateTable.map( (obj, index) => {
            if (obj.uid === data){
                console.log("index ", index);
                dispatch(saveDataStore(obj));
            }
        })
    }
    const openWindow = (nameWindow, typeRequest) => {
        dispatch(saveTypeRequest(typeRequest));
        dispatch(saveNameWindow(nameWindow));
    }

    const HendlerOnMouseover = () => {

    }
    const HendlerOnMouseLeave = () => {

    }

 useEffect(() => {
     console.log(' useEffect GET ');
     dispatch(startGetRequestServer());
    }, [])




    const styles = { loading: "Loading" };

    if (stateLoading) {
        return <AwaitLoading
                    classname={styles.loading}
                />
    } 
        return (
            <div>
                <HeaderDictionary
                    classname=""
                    nameHeader="Р’С‹РІРѕРґ РґР°РЅРЅС‹С… View Type"
                />

                <ConsoleButton
                    classname=""

                    linkWindowEdit={linkAddressWindowEdit}
                    linkWindowConfirmDeleting={linkAddressWindowDelete}

                    onmouseover={HendlerOnMouseover}
                    onmouseleave={HendlerOnMouseLeave}

                    name1="РћР±РЅРѕРІРёС‚СЊ"
                    title1="РћР±РЅРѕРІРёС‚СЊ"
                    onclick1={() => { dispatch(startGetRequestServer()) }}


                    name2="Р”РѕР±Р°РІРёС‚СЊ"
                    title2="Р”РѕР±Р°РІРёС‚СЊ"
                    onclick2={() => openWindow("РќРѕРІРѕРµ Р·РЅР°С‡РµРЅРёРµ", "post")}

                    name3="Р’РЅРµСЃС‚Рё РёР·РјРµРЅРµРЅРёСЏ"
                    title3="Р’РЅРµСЃС‚Рё РёР·РјРµРЅРµРЅРёСЏ"
                    onclick3={() => openWindow("РР·РјРµРЅРёС‚СЊ Р·РЅР°С‡РµРЅРёРµ", "put")}

                    name4="РўРѕР»СЊРєРѕ Р°РєС‚РёРІРЅС‹Рµ"
                    title4="РўРѕР»СЊРєРѕ Р°РєС‚РёРІРЅС‹Рµ"
                    onclick4={() => console.log('click')}

                    name5="РЈРґР°Р»РёС‚СЊ"
                    title5="РЈРґР°Р»РёС‚СЊ"
                    onclick5={() => console.log('click')}

                    type="button"
                    tabindex="-1"

                />

                <TableList
                    columnName={Object.keys(stateTable[0])}
                    dataTable={stateTable}
                    onclick={(event, data) => handlerTableRowUID(event, data)}
                />

            </div>
        )

}

export default connect()(ViewType)
