import {GET_REQUEST, POST_REQUEST, PUT_REQUEST} from "../types";

const initialState =[
          {}
        ]


const stateTable = (state = initialState, action) => {
    
    switch (action.type){
            
        case GET_REQUEST:
            return  action.payload
            
        case POST_REQUEST:
            return  [
                ...state,
                action.payload
            ]
            
        case PUT_REQUEST:
            state.map ( obj =>{
                if (obj.uid == action.payload.uid){
                    obj = action.payload
                }
            })
            
            
            return  state
 
        default: 
            return state;
    }
}
export default stateTable
