import React, { useState} from 'react';

function RowTable (props){
    
    const Keys = Object.keys(props.dataRow);
    const uid = props.dataRow.uid;

    const inputChecked = data => {
        if (data === true) {
            return 'checked'
        } else {
            return false
        };
    }
    const [rowBackground, setRowBackground] = useState("red");
    const focusUid = props.focusUid;

 //   if (props.dataRow.uid == props.focusUid) {
  //      setRowBackground("red");
 //   }

    return(

        <tr style={{ rowBackground}}>
            {
                (props.number <= props.CountRow)?
                    <td> {props.number}</td>
                    :null
            }
            
            {
                Keys.map((keyObj, index) => {

                    if (props.number <= props.CountRow) {

                            if (keyObj === 'name') {
                                return <td
                                            key={index}
                                            uid={props.dataRow.uid}
                                            onClick={(event) => props.onclick(event, uid)}
                                        >
                                            {props.dataRow[keyObj]}

                                </td>
                            }

                            if (keyObj === 'isDeleted' || keyObj === 'isTemplate') {
                                return <td
                                    key={index}
                                    uid={props.dataRow.uid}
                                    onClick={(event) => props.onclick(event, uid)}
                                >
                                    <input
                                        type="checkbox"
                                        checked={inputChecked(props.dataRow[keyObj])}
                                        disabled="disabled"

                                    /><span className="fa fa-check" aria-hidden="true"></span>
                                </td>
                            }
                    }

                    
                }) 
            }
        
        </tr>
    )
}

export default RowTable
