import React, { useState} from 'react';

import ButtonLink from '../../UserInterface/Buttons/Button/ButtonLink';
import NavigationWindowBlind from '../../UserInterface/Navigation/NavigationWindowBlind';
import NavigationLinkAbout from '../../UserInterface/Navigation/NavigationLinkAbout';
import NavigationToggle from '../../UserInterface/Navigation/NavigationToggle';

import hoverDescription from '../../hoc/hoverDescription';

//import iceBaikal from '../../image/iceBaikal.jpg';
//import { NavLink } from 'react-router-dom';

import ButtonLinkSmall from '../../UserInterface/Buttons/Button/ButtonLinkSmall';


import { aboutLinks } from "../../constans/aboutLinks";


function Header() {

   
    const [linkAbout, setLinkAbout] = useState("");
    const [isOpen, setIsOpen] = useState(false);
    const [isOpenAbout, setIsOpenAbout] = useState(false);

    const navStyle = ["Navigation"];
    if (!isOpen) {
        navStyle.push("close");
    };

    const hendlerHoverLink = (data) => {
        setLinkAbout(aboutLinks[data]);
        setIsOpenAbout(true);
    }
    const hendlerLeaveLink = () => {
        setLinkAbout("");
        setIsOpenAbout(false);
    }
    const hendlerToggleNavigation = () => {
        setIsOpen(!isOpen);
    }
    const hendlerCloseNavigation = () => {
        setIsOpen(false);
        setIsOpenAbout(false);
    }

    const pages = [
        {
            to: '/',
            name: 'Home'
        },
        {
            to: '/EltypeClass',
            name: 'EltypeClass'
        },
        {
            to: '/ViewType',
            name: 'ViewType'
        },
    ]

    return (
        <header>
            <nav className={navStyle.join(' ')}>

                    <ButtonLinkSmall
                            to='/Personalize'
                            icon="fa fa-user-circle-o"
                            classname="PersonalizeButton"
                            onmouseover={() => hendlerHoverLink('Personalize')}
                            onmouseleave={hendlerLeaveLink}
                            onclick={hendlerCloseNavigation}
                            tabindex="-1"
                    />
                    {
                        pages.map((page, index) => 
                                <ButtonLink
                                            to={page.to}
                                            name={page.name}
                                            onclick={hendlerCloseNavigation}
                                            onmouseover={() => hendlerHoverLink([page.name])}
                                            onmouseleave={hendlerLeaveLink}
                                            classname='NavigationButtonLink'
                                            tabIndex={index + 1}
                                            key={index +1}
                                    />
                        )
                    }
                <ButtonLink
                    to='/Personalize'
                    name='Personalize'
                    classname='NavigationButtonLink'
                    onclick={hendlerCloseNavigation}
                />

            </nav>
            {
                isOpenAbout ?
                    <NavigationLinkAbout
                        aboutLink={linkAbout}
                        classname="NavigationLinkAbout"
                    />
                    : null
            }
            {
                isOpen ?
                    <NavigationWindowBlind
                        isOpen={isOpen}
                        onClick={hendlerCloseNavigation}
                        classname="NavigationWindowBlind"
                    />
                    : null
            }
            
            <NavigationToggle
                openNavigation={hendlerToggleNavigation}
                isOpen={isOpen}
                classname='NavigationToggle'
            />

        </header>
    )
}
export default Header
