import React, { useEffect, useState} from 'react';
import { connect, useDispatch, useSelector } from "react-redux";

import ConsoleButton from '../../components/ConsoleButton/ConsoleButton';
import TableList from '../../components/Table/TableListEltypeClass';
import HeaderDictionary from '../../components/Header/HeaderDictionary';
import AwaitLoading from '../../UserInterface/AwaitLoading/AwaitLoading';

import {
        startGetRequestServerEltypeClass,
        saveDataStore,
        saveTypeRequest,
        saveNameWindow,
 } from '../../store/EltypeClass/actions/actionEltypeClass';

import {
    changeCountRowTable,
    getCounterPagesTable,
    changeNumberList,
    getDataPageRowTable
} from '../../store/EltypeClass/actions/actionsFlagsEltypeClass';

import { linkEditWindowEltypeClass } from '../Navigation/Main';
import { linkWindowConfirmDeleting } from '../Navigation/Main';

import { tablebodyBackground_Coffee, rowFocus_Coffee } from '../../styles/const_color';




function EltypeClass(props) {

    const linkAddressWindowEdit = linkEditWindowEltypeClass;
    const linkAddressWindowDelete = linkWindowConfirmDeleting;
    
    const dispatch = useDispatch();

    const stateLoading = useSelector(state => state.stateFlagsEltypeClass.loading);

    const stateTable = useSelector(state => state.stateTableEltypeClass); //ВСЯ база

    const dataTable = useSelector(state => state.stateFlagsEltypeClass.dataListTable); // отображаемый лист

    const counterPages = useSelector(state => state.stateFlagsEltypeClass.counterPagesTable); // общее количество листов в таблице

    const countRow = useSelector(state => state.stateFlagsEltypeClass.countRowTable); // количество строк на отображаемой странице

    const numberList = useSelector(state => state.stateFlagsEltypeClass.numberList); //номер отображаемого листа

    const focusData = useSelector(state => state.createDataEltypeClass.stateData); // запомнить данные выбранной строки


    const tablebodyBackgroundClear = () => {
        let tablebody = document.getElementById('tablebody');
        for (let child of tablebody.children) {
            child.style.background = tablebodyBackground_Coffee;//убираем цвет ранее выбранной строки
        };
    }
    const focusNull = () => {
        tablebodyBackgroundClear();
        dispatch(saveDataStore({
                                uid: "",
                                name: "",
                                isDeleted: false,
                                isTemplate: false
                            }));
    }

    //правая кнопка мыши
    document.oncontextmenu = function (event) {
        if (event.defaultPrevented) return;
        event.preventDefault();
        if (event.target.tagName == 'TD') {
            console.log('правая кнопка мыши');
        };
    };

    const handlerTableRowUID = (event, data)=>{ //фокус на строку
        event.preventDefault();

        let tr = event.target.parentElement; //строка выбранной ячейки
        tr.children[1].focus();
        tablebodyBackgroundClear();
        tr.style.background = rowFocus_Coffee;

        stateTable.map( (obj, index) => {
            if (obj.uid === data){
                dispatch(saveDataStore(obj)); // сохранить в хранилище
            }
        })
    }
    const hendlerOpenWindow = (nameWindow, typeRequest) => {
        dispatch(saveTypeRequest(typeRequest));
        dispatch(saveNameWindow(nameWindow));
    }
    const HendlerOnMouseover = () => {

    }
    const HendlerOnMouseLeave = () => {

    }

    const [flagCountRow, setFlagCoutnRow] = useState(false);


    const hendlerInputOpen = () => {
        setFlagCoutnRow(true);
    }

    const hendlerChangeCountRow = (event) => {
            event.preventDefault();
            dispatch(changeCountRowTable(event.target.value));
            dispatch(changeNumberList(1)); // при изменении к-ва строк на странице всегда возвращаемся на страницу №1, если это не надо, убрать эту строку, но может появляться путой лист
            if (numberList != 1) {
                      focusNull();  // переходим на страницу 1, фокус теряется, поэтому в хранилище обнуляем данные
                  };
        
    }

    const hendlerInputSave = () => {

        setFlagCoutnRow(false);
        dispatch(getCounterPagesTable(stateTable, countRow));
        dispatch(getDataPageRowTable(stateTable, countRow, numberList));
    }
    //после выбора страницы приходит ее номер
    // в хранилище собираем новую страницу
    // в хранилище сохраняем номер страницы
    const hendlerOpenPagesTable = (num) => {

        dispatch(getDataPageRowTable(stateTable, countRow, num));
        dispatch(changeNumberList(num));
        focusNull(); // при переходе на другую страницу фокус теряется, поэтому в хранилище обнуляем данные
    }


 useEffect(() => {
     dispatch(startGetRequestServerEltypeClass());

    }, [])


    const stateConsoleButton = [
        {
            text: "Обновить",
            icon: "fa fa-download fa-lg",
            onclick: () => { dispatch(startGetRequestServerEltypeClass()) }
            },
        {
            text: "Добавить новые данные",
            icon: "fa fa-plus fa-lg",
            onclick: () => hendlerOpenWindow("Новые данные", "post")
            },
        {
            text: "Внести изменения",
            icon: "fa fa-pencil-square-o fa-lg",
            onclick: () => hendlerOpenWindow("Внести изменения", "put")
            },
        {
            text: "Удалить",
            icon: "fa fa-times fa-lg",
            onclick: () => {}
            },
        {
            text: "Показать только активные",
            icon: "fa fa-filter fa-lg",
            onclick: () => console.log('click')
            }
    ];
    
    return (
        <div className="DataBase__container">
 
                <div className="DataBase__header__container">
                    <ConsoleButton
                            classnameWrapper="DataBase__header__console"

                            linkWindowEdit={linkAddressWindowEdit}
                            linkWindowConfirmDeleting={linkAddressWindowDelete}
        
                            stateConsoleButton = {stateConsoleButton}

                            onmouseover={HendlerOnMouseover}
                            onmouseleave={HendlerOnMouseLeave}

                            tabindex="-1"

                            />

                    <HeaderDictionary
                            classname="DataBase__header__header"
                            nameHeader="Классы для Типов связей и Типов сущностей"
                        />
                </div>
                {
                    stateLoading?
                        <AwaitLoading
                                classname="Loading"
                        />
                       :<TableList
                                classname="DataBase__table__container"
                                classnameConsoleCountRow='ConsoleCountRow'
                                classnameButtonConsoleCountPow="PersonalizeButton"
                                columnName={Object.keys(stateTable[0])}
                                dataTable={dataTable}
                                onclick={(event, data) => handlerTableRowUID(event, data)}
                                onclickInputOpen={hendlerInputOpen}
                                onclickInputSave={hendlerInputSave}
                                onclickOpenPagesTable={(numButton) => hendlerOpenPagesTable(numButton)}
                                flagCountRow={flagCountRow}
                                onchange={(event) => hendlerChangeCountRow(event)}
                                CountRow={countRow}
                                counterPages={counterPages}
                                focusUid={focusData.uid}
                        /> 
                }

        </div>
    )

}

export default connect()(EltypeClass)
