import React from 'react';
import { connect, useDispatch, useSelector } from "react-redux";
import InputDatalist from '../../UserInterface/Input/InputDatalist';
import Checkbox from '../../UserInterface/Input/Checkbox';
import { countries } from '../../constans/countries';
import { haveDescriptionAll } from '../../store/Flags/actionsFlags';


function Home(props) {

    const flagHaveDescription = useSelector(state => state.stateFlagsAll.haveDescriptionAll);
    const dispatch = useDispatch();

    console.log('flagHaveDescription ', flagHaveDescription);

    const inputChecked = flag => {
        if (flag == true) {
            return "checked"
        } else {
            return ""
        }
    }
    const hendlerHaveDescription = (event) => {
        if (event.target.type == "checkbox") {
            let flag = !flagHaveDescription;
            dispatch(haveDescriptionAll(flag));
        }
    }

    return(
         <div>
                <div className="">
                    HOME
                </div>

            <InputDatalist
                arrValue={countries}
                id="countriesInput"
                listId="countriesListId"
                placeholder="country"
                onchange={() => console.log('Input coutry')}
                tabindex="1"
                autocomplete = "off"
            />
            <Checkbox
                label = "Показывать всплывающие подсказки"
                id="haveDescription"
                type='checkbox'
                data={'haveDescription'}
                onchange={hendlerHaveDescription}
                checked={inputChecked(flagHaveDescription)}
                classNameLabel = ''
                classNameCheckbox=''
            />

                
        </div>
    )
}

export default connect(Home)
