import {
    SHOW_LOADER,
    COUNTER_PAGES_TABLE,
    COUNT_ROW_TABLE
                    } from "../types";

export const loadingShow = (data) => {

    return {
        type: SHOW_LOADER,
        payload: data
    }
}

export const getCounterPagesTable = (dataFromServer) => {

    return {
        type: COUNTER_PAGES_TABLE,
        payload: dataFromServer
    }
}
export const getCountRowTable = (data) => {

    return {
        type: COUNT_ROW_TABLE,
        payload: data
    }
}
