import {
    SHOW_LOADER,
    COUNTER_PAGES_TABLE
                    } from "../types";

const initialState = {
                loading: false,
                counterPagesTable: 0,
                countRowTable: 10
            };


const stateFlagsEltypeClass = (state = initialState, action) => {

    switch (action.type) {

        case SHOW_LOADER:
            return {
                ...state,
                loading: action.payload
            }
        case COUNTER_PAGES_TABLE:
            return {
                ...state,
                counterPagesTable: action.payload
            }

        default:
            return state;
    }
}
export default stateFlagsEltypeClass
