import {
    ADD_DATA_COMPARE,
    CLEAR_ARR_COMPARE

} from "../types";

export const addDataCompareArr = (data, arr) => {

    if (arr.length > 0) {
            let dataFind = arr.find(obj => obj.uid == data.uid);

            console.log('dataFind ', dataFind);

            if (dataFind == undefined) {
                return {
                    type: ADD_DATA_COMPARE,
                    payload: arr.push(data)
                };
            } else {
                return {
                    type: ADD_DATA_COMPARE,
                    payload: arr
                };
        };

    } else {

        return {
            type: ADD_DATA_COMPARE,
            payload: arr.push(data)
        };
    }

   
};
