import React from 'react';
import { connect } from "react-redux";
import { saveDataStore } from '../store/EltypeClass/actions/actionEltypeClass';
import { tablebodyBackground_Coffee, rowFocus_Coffee } from '../styles/const_color';

const mapStateToProps = (state) => {
    return {
        focusData: state.createDataEltypeClass.stateData, //что хранится в фокусе
        stateTable: state.stateTableEltypeClass //ВСЯ база
    }
}

const mapDispatchToProps = {
    saveDataStore
}


const withClickSaveFocusRow = (Component) => {

    class ComponentWithClickSaveFocus extends React.Component {
        constructor(props) {
            super(props);
            this.tr = '';
            this.state = {
                tableBackground: tablebodyBackground_Coffee,
                trBackground: trowFocus_Coffee
            }
        }

        handlerTableRowUID = (event, data) => { //фокус на строку
            event.preventDefault();

            if (event.target.parentElement.tagName == 'TR') {
                this.tr = event.target.parentElement;

                this.props.stateTable.map((obj) => {
                    if (obj.uid === data) {
                        this.props.saveDataStore(obj); // сохранить в хранилище для изменений
                    }
                })
            } else if (event.target.parentElement.tagName == 'TD') {
                this.tr = event.target.parentElement.parentElement;

                this.props.stateTable.map((obj) => {
                    if (obj.uid === data) {
                        this.props.saveDataStore(obj); // сохранить в хранилище для изменений
                    }
                })
            } else if (event.target.parentElement.tagName == 'DIV') {
                this.tr = event.target.parentElement.parentElement.parentElement; //просто выделить, удаление идет через кнопку
            };

            let tablebody = document.querySelector('tbody');
            for (let child of tablebody.children) {
                child.style.background = 'red';//убираем цвет ранее выбранной строки
             
            }; // очищаем поле
            this.tr.style.background = "rgba(95, 48, 25, 0.4)"; // выделяем цветом строчку
        }
        render() {
            let props = {
                ...this.props,
                onclick: this.handlerTableRowUID,
                backgroundTbody: this.state.tableBackground,
                backgroundTR: this.state.trBackground
            }
            return (
                <Component {...props} />
                )
        }
    }
    return connect(mapStateToProps, mapDispatchToProps)(ComponentWithClickSaveFocus)
}
export default withClickSaveFocusRow
