import {
    HAVE_DESCRIPTION_ALL
    
                    } from "./types";

const initialState = {
                haveDescriptionAll: true

            };


const stateFlagsAll = (state = initialState, action) => {

    switch (action.type) {

        case HAVE_DESCRIPTION_ALL:
            return {
                ...state,
                haveDescriptionAll: action.payload
            }
 
        default:
            return state;
    }
}
export default stateFlagsAll
