import React from 'react';

import RowTable from './RowTable';
import ConsoleCountRow from './ConsoleCountRow';
import ButtonSmall from '../../UserInterface/Buttons/Button/ButtonSmall';
import InputDatalist from '../../UserInterface/Input/InputDatalist';

function TableList(props) {
    
    return(
        <div className={props.classname}>
            <table>
                  <thead>
                    <tr>
                        <th>
                            №
                        </th>
                    {
                        props.columnName.map((name, index) => {
                            if (name === 'name' ) {
                                return <th
                                            key={index}
                                        >
                                            <div> {name} </div>
                                            <div>
                                                    <ButtonSmall
                                                        icon="fa fa-arrow-down"
                                                        onclick={props.onclickSortBase}
                                                        data={name}
                                                    />
                                                    <ButtonSmall
                                                        icon="fa fa-indent"
                                                        onclick={props.onclickSortList}
                                                        data={name}
                                                    />
                                                    {
                                                        props.flagInputSearch ?
                                                            <div>
                                                                <InputDatalist
                                                                        id='NameBase'
                                                                        listId='SearchNameBase'
                                                                        placeholder='Поиск по наименованию'
                                                                        arrValue={props.dataBase}
                                                                        autocomplete='off'
                                                                        onchange={props.onchangeSearch}
                                                                    />
                                                                <ButtonSmall
                                                                        icon="fa fa-window-close-o"
                                                                        onclick={props.onclickCloseInputSearch}
                                                                    />
                                                             </div>
                                                            :<ButtonSmall
                                                                icon="fa fa-search"
                                                                onclick={props.onclickOpenInputSearch}
                                                            />
                                                    }
                                            </div>
                                        </th>
                            }
                        })
                    }
                    {
                        props.columnName.map((name, index) => {
                            if (name === 'isDeleted' || name === 'isTemplate') {
                                return <th
                                            key={index}
                                        >
                                            <div>{name}</div>

                                            <div>
                                                    <ButtonSmall
                                                        icon="fa fa-arrow-down"
                                                        onclick={props.onclickSortBase}
                                                        data={name}
                                                    />
                                                    <ButtonSmall
                                                        icon="fa fa-indent"
                                                        onclick={props.onclickSortList}
                                                        data={name}
                                                    />
                                            </div>
                                        </th>
                            }
                        })
                    }
                    </tr>
                  </thead>

                  <tbody id="tablebody">
                    {
                        props.dataTable.map((data, index) => <RowTable
                                                                    dataRow={data}
                                                                    key={index}
                                                                    number={index + 1}
                                                                    onclick={props.onclick}
                                                                    CountRow={props.CountRow}
                                                                    
                                                            />
                                            )
                    }
                    </tbody>
            </table>
            <ConsoleCountRow
                classname={props.classnameConsoleCountRow}
                classnameButtonConsoleCountRow={props.classnameButtonConsoleCountPow}
                onclick={props.onclickOpenPagesTable}
                onclickInputOpen={props.onclickInputOpen}
                onclickInputSave={props.onclickInputSave}
                onchange={props.onchangeCountRow}
                CountRow={props.CountRow}
                counterPages={props.counterPages}
                flagCountRow={props.flagCountRow}
                lenght={props.dataTable.lenght}
                numberActivList={props.numberActivList}
            />
    </div>
    )
}

export default TableList
