import React, { useState} from 'react';
import { NavLink } from 'react-router-dom';
import NavigationLinkAbout from '../../Navigation/NavigationLinkAbout';


function ButtonLinkNavigation (props){
    
    const [isOpenAbout, setIsOpenAbout] = useState(false);
    
    const hendlerOpenAbout = () => {
            setIsOpenAbout(!isOpenAbout);
        }
    
    return(
        <React.Fragment>
           
                <div 
                        className={props.classname}
                        onClick={() => props.onclick()}
                        tabIndex={props.tabindex}
                        onmouseover={hendlerOpenAbout}
                        onmouseleave={hendlerOpenAbout}
                >
                    <NavLink 
                            to={props.to}
                            exact={props.exact}
                    > 
                        {props.name}

                    </NavLink>
                    <NavigationLinkAbout
                                aboutLink={props.aboutLink}
                                classname={props.classnameLinkAbout}
                                />

                </div>

        </React.Fragment>
    )
}

export default ButtonLinkNavigation

