import {
    SHOW_LOADER,
    STATUS_CONFIRM_DELETE,
    STATUS_OPEN_EDIT_WINDOW,
    STATUS_WARNING
                    } from "../types";

const initialState = {
                loading: false,
                confirmDeleting: false,
                openEditWindow: false,
                warning: false
            };


const stateFlagsEltypeClass = (state = initialState, action) => {

    switch (action.type) {

        case SHOW_LOADER:
            return {
                ...state,
                loading: action.payload
            }
            
        case STATUS_CONFIRM_DELETE:
            console.log('STATUS_CONFIRM_DELETINGg ', action.payload)
            state.confirmDeleting= !state.confirmDeleting
            return state;
                
        case STATUS_OPEN_EDIT_WINDOW:
            return {
                ...state,
                openEditWindow: action.payload
            }
        case STATUS_WARNING:
            console.log('STATUS_WARNING ', action.payload)
            state.warning = !state.warning
            return state;


        default:
            return state;
    }
}
export default stateFlagsEltypeClass
