import {
    ADD_DATA_COMPARE,
    CLEAR_ARR_COMPARE,
    DELETE_DATA_COMPARE

} from "../types";

export const addDataCompareArr = (data, arrOld) => {
    
    let arr = [];
    for (let i = 0; i < arrOld.length; i++) {
        if (arrOld[i].uid != data.uid && arrOld[i] != {} && arrOld[i].uid != '') {
            arr.push(arrOld[i]);
        }
    };
    arr.push(data);

                return {
                    type: ADD_DATA_COMPARE,
                    payload: arr
                };

};

export const clearCompareArr = () => {
    return {
        type: CLEAR_ARR_COMPARE,
        payload: []
    };
};

export const deleteDataCompareArr = (data, arrOld) => {
    let arr = arrOld.filter(obj => obj.uid !== data);

    return {
        type: DELETE_DATA_COMPARE,
        payload: arr
    };
}
