import React from 'react';
import { connect } from "react-redux";
import { saveDataStore } from '../store/EltypeClass/actions/actionEltypeClass';

import { focusRowColorBackground } from '../functions/focusRowColorBackground';
import { tablebodyBackground_Coffee, rowFocus_Coffee } from '../styles/const_color';

const allRowColorBackground = tablebodyBackground_Coffee; //цвет поля таблицы
const colorFocusRowBackground = rowFocus_Coffee; //цвет выбранной в фокус строки

const mapStateToProps = (state) => {
    return {
        focusData: state.createDataEltypeClass.stateData, //что хранится в фокусе
    }
}

const mapDispatchToProps = {
    saveDataStore
}


const withClickSaveFocusRow = (Component, base) => {


        
    class ComponentWithClickSaveFocus extends React.Component {

        handlerTableRowUID = (event, data) => { //фокус на строку
            event.preventDefault();
            if (event.target.parentElement.tagName === 'TR' || event.target.parentElement.tagName === 'TD' || event.target.parentElement.tagName === 'DIV') {

               


                base.map((obj, index) => {
                    if (obj.uid === data) {
                        this.props.saveDataStore(obj); // сохранить в хранилище для изменений
                        const tablebody = document.querySelector('tbody');

                        //child.style.background = allRowColorBackground;//убираем цвет ранее выбранной строки

                        for (let child of tablebody.children) {
                            
                            if (child.children[1].innerHTML !== undefined && child.children[1].innerHTML === obj.name) {

                                child.style.background = colorFocusRowBackground; //выделяем цветом строку фокуса
                                console.log('условие выполнено')
                            } else { console.log('условие выделения строки не выполнено') };
                        };
                    };
                });
            };
      }

        render() {
            let props = {
                ...this.props,
                onclick: this.handlerTableRowUID
            }
            return (
                <Component {...props} />
                )
        }
    }
    return connect(mapStateToProps, mapDispatchToProps)(ComponentWithClickSaveFocus)
}
export default withClickSaveFocusRow
