import React from 'react';
import { connect, useDispatch, useSelector } from "react-redux";
import Authorization from '../Authorization/Authorization';
import WarningLoginNotConfimed from '../../components/WarningWindow/WarningLoginNotConfimed';
import {
    confirmLogin,
    flagRepeatPasswordAuth
} from '../../store/Authorization/actionsAuthorization';


function Home(props) {
    const dispatch = useDispatch();
    const flagAuth = useSelector(state => state.stateAuthorization.user.password); 
    const flagLoginConfirmed = useSelector(state => state.stateAuthorization.flagLoginConfirmed); 
    const flagPasswordConfirmed = useSelector(state => state.stateAuthorization.flagPasswordConfirmed); 


    const hendlerСomeBackLoginAuth = () => {
        dispatch(confirmLogin({ title: '' }));
    }
    const hendlerСomeBackPasswordAuth = () => {
        dispatch(flagRepeatPasswordAuth(true));  // в окне WarningPasswordNotConfimed нажали на кнопку вернуться в LoginAuth
    }
    return(
        <div>
            {
                flagAuth === "notConfirmed" ?
                    <Authorization
                        flag={flagPasswordConfirmed}
                        onclick={hendlerСomeBackPasswordAuth}
                        classname=""
                    />
                    :
                    <div className="">
                        HOME
                        
                        {
                            flagLoginConfirmed ?
                                <Authorization
                                    flag
                                    onclick={hendlerСomeBackPasswordAuth}
                                    classname=""
                                />
                                : <WarningLoginNotConfimed
                                    onclick={hendlerСomeBackLoginAuth}
                                    classname=""
                                />
                        }

                    </div>
            }
            
           
                 

            


                
        </div>
    )
}

export default connect()(Home)
