import React from 'react';
import { connect, useSelector } from "react-redux";

const hoverDescription = (Component, propIsOpen) => {

    const flagHaveDescription = useSelector(state => state.stateFlagsAll.haveDescriptionAll);

    return class extends React.Component {

        state = { isOpen: false };
        mouseOver = () => this.setState({ isOpen: true });
        mouseOut = () => this.setState({ isOpen: false });

        render() {

            const props = {
                [propIsOpen]: this.state.isOpen,
                ...this.props,
            }

            return(

                <div
                    onMouseOver={this.mouseOver}
                    onMouseOut={this.mouseOut}
                    onClick={this.mouseOut}
                >
                    <Component {...props} />
                </div>
                )
        }
    }

}
export default connect()(hoverDescription)
