import {
    CREATE_DATA,
    TYPE_REQUEST,
    NAME_WINDOW
} from "../types"

const initialState ={
    flag: false,
    stateData: {
                uid: 0,
                name: "",
                comment: "",
                classViewProperty: "",
                dbTable: ""
            },
    nameWindow: "o",
    typeRequest: null,
    labels: [
                'uid',
                'Наименование',
                'Примечания',
                'classViewProperty',
                'dbTable'
            ],
    placeholders: [
                'uid',
                'Введите полное наименование',
                'Дополнения и комментарии',
                'classViewProperty',
                'dbTable'
            ],
    typeData: [
                "number",
                "text",
                "text",
                "text",
                "text"
            ]
                    };

const WindowCreateNewData = (state = initialState, action) => {
    
    switch (action.type){
            
        case CREATE_DATA:
            return {
                ...state,
                stateData: action.payload
            }
   
        case TYPE_REQUEST:
            return {
                ...state,
                typeRequest: action.payload
            }
    
        case NAME_WINDOW:
            return {
                ...state,
                nameWindow: action.payload
            }

        default: 
            return state;
    }
}
export default WindowCreateNewData
