import React, { useState} from 'react';
import { connect, useDispatch } from "react-redux";
import ButtonLinkNavigation from '../../UserInterface/Buttons/Button/ButtonLinkNavigation';
import NavigationWindowBlind from '../../UserInterface/Navigation/NavigationWindowBlind';

import NavigationToggle from '../../UserInterface/Navigation/NavigationToggle';
import { startGetRequestServer } from '../../store/serverViewType/actions/actionViewType';
//import iceBaikal from '../../image/iceBaikal.jpg';
//import { NavLink } from 'react-router-dom';

import "../../styles/Navigation.scss";
import '../../styles/NavigationToggle.scss';
import '../../styles/NavigationWindowBlind.scss';
import { aboutLinks } from "../../constans/aboutLinks";


function Header() {

    const dispatch = useDispatch();

   
    const [linkAbout, setLinkAbout] = useState("");
    const [isOpen, setIsOpen] = useState(false);

    const navStyle = ["Navigation"];
    if (!isOpen) {
        navStyle.push("close");
    };

    const hendlerHoverLink = (data) => {
        setLinkAbout(aboutLinks[data]);
    }
    const hendlerLeaveLink = () => {
        setLinkAbout("");
    }
    const hendlerToggleNavigation = () => {
        setIsOpen(!isOpen);
    }
    const hendlerCloseNavigation = () => {
        setIsOpen(false);
    }

    return (
        <header>
            <nav className={navStyle.join(' ')}>
 
                <ButtonLinkNavigation
                        to='/'
                        name='Home'
                        onclick={hendlerCloseNavigation}
                        onmouseover={() => hendlerHoverLink('Home')}
                        onmouseleave={hendlerLeaveLink}
                        classname='NavigationButtonLink'
                        tabIndex='1'
                        aboutLink={linkAbout}
                        classnameLinkAbout="navigationLinkAbout"
                    />
                <ButtonLinkNavigation
                        to='/ViewType'
                        name='ViewType'
                        onclick={hendlerCloseNavigation}
                        onmouseover={() => hendlerHoverLink('ViewType')}
                        onmouseleave={hendlerLeaveLink}
                        classname='NavigationButtonLink'
                        tabIndex='2'
                        aboutLink={linkAbout}
                        classnameLinkAbout="navigationLinkAbout"
                    />
                <ButtonLinkNavigation
                        to='/EltypeClass'
                        name='EltypeClass'
                        onclick={hendlerCloseNavigation}
                        onmouseover={() => hendlerHoverLink('EltypeClass')}
                        onmouseleave={hendlerLeaveLink}
                        classname='NavigationButtonLink'
                        tabIndex='3'
                        aboutLink={linkAbout}
                        classnameLinkAbout="navigationLinkAbout"
                 />

            </nav>

            {
                isOpen ?
                    <NavigationWindowBlind
                        isOpen={isOpen}
                        onClick={hendlerCloseNavigation}
                        classname="NavigationWindowBlind"
                    />
                    : null
            }
            
            <NavigationToggle
                openNavigation={hendlerToggleNavigation}
                isOpen={isOpen}
                classname='NavigationToggle'
            />

        </header>
    )
}
export default connect()(Header)

