import React from 'react';

import ButtonSmall from '../../UserInterface/Buttons/Button/ButtonSmall';
import withHoverDescription from '../../hoc/withHoverDescription';
import DescriptionButton from '../../components/Description/DescriptionButton';
import Input from '../../UserInterface/Input/Input';


function CountRowForPage(props) {

    // логика всплывающей подсказки спрятана в hoc, обертка кнопок и подсказка реализованы в ButtonConsoleTable
    const ButtonOpenInputChangeCountRow = withHoverDescription(ButtonSmall, DescriptionButton);
    const ButtonSaveChangeCountRow = withHoverDescription(ButtonSmall, DescriptionButton);

    return (

        <>
          <div>
                {
                    props.flagCountRow ?
                        <Input
                            id="inputCountRow"
                            type="number"
                            label="Строк на странице : "
                            onchange={props.onchange}
                            value={props.CountRow}
                            tabindex="-1"
                        />
                        : <div> Строк на странице : <span> {props.CountRow} </span> из {props.length}</div>
                }
           </div>
                {
                    props.flagCountRow ?
                    <ButtonSaveChangeCountRow
                            className={props.classnameButtonConsoleCountRow}
                            onclick={props.onclickInputSave}
                            tabindex='-1'
                            icon="fa fa-floppy-o"
                            name="SaveChangeCountRow"
                            classnameDescription='DescriptionButton'
                            positionDescription="top"
                        />
                    : <ButtonOpenInputChangeCountRow
                            className={props.classnameButtonConsoleCountRow}
                            onclick={props.onclickInputOpen}
                            tabindex='-1'
                            icon="fa fa-pencil"
                            name="OpenInputChangeCountRow"
                            classnameDescription='DescriptionButton'
                            positionDescription="botton"
                        />
                 }

            
        </ >

    )
}

export default CountRowForPage
