import React, { useRef, useEffect, useState} from 'react';

function RowTable(props) {

    const rowRef = useRef(null);
    
    const Keys = Object.keys(props.dataRow);
    const uid = props.dataRow.uid;

    const inputChecked = data => data ? 'checked' : false

    const [flagFocus, setFlagFocus] = useState(false);
    const [colorRow, setColorRow] = useState({ color: 'blue' });

    const hendlerClick = (event) => {
        props.onclick(event, uid);
        setFlagFocus(!flagFocus);
        setColorRow({ color: 'red' });
        console.log('REF ', rowRef.current )
    }

    useEffect(() => {
        if (flagFocus === true) {
            rowRef.current.focus();
            setColorRow({ color: 'red' });
        }
        return setColorRow({ color: 'black' });
    }, [flagFocus])

    return(

        <tr style={colorRow}>
            {
                (props.number <= props.CountRow)?
                    <td> {props.number}</td>
                    :null
            }
            
            {
                Keys.map((keyObj, index) => {

                    if (props.number <= props.CountRow) {

                        if (props.typeDataColumns[index] === 'text') {
                                return <td
                                    ref={rowRef}
                                    key={index}
                                    uid={props.dataRow.uid}
                                    onClick={hendlerClick}
                                        >
                                            {props.dataRow[keyObj]}

                                </td>
                            }

                        if (props.typeDataColumns[index] === 'checkbox') {
                                return <td
                                        key={index}
                                        uid={props.dataRow.uid}
                                        onClick={hendlerClick}
                                >
                                    <input
                                        type="checkbox"
                                        checked={inputChecked(props.dataRow[keyObj])}
                                        disabled="disabled"

                                    /><span className="fa fa-check" aria-hidden="true"></span>
                                </td>
                            }
                    }

                    
                }) 
            }
        
        </tr>
    )
}

export default RowTable
